import React from "react";
import Die from "../src/Die";

function randomDiceRoll() {
  const numbers = ["one", "two", "three", "four", "five", "six"];
  return numbers[Math.floor(Math.random() * numbers.length)];
}

class RollDice extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isRolling: false,
      die1: "one",
      die2: "two"
    };
  }
  onRollDice = e => {
    const die1 = randomDiceRoll();
    const die2 = randomDiceRoll();
    this.setState({
      die1: die1,
      die2: die2,
      isRolling: true
    });
    setTimeout(() => {
      this.setState({
        isRolling: false
      });
    }, 1000);
  };
  render() {
    return (
      <div>
        <Die dieNumber={this.state.die1} isRolling={this.state.isRolling} />
        <Die dieNumber={this.state.die2} isRolling={this.state.isRolling} />
        <button onClick={this.onRollDice} disabled={this.state.isRolling}>
          {this.state.isRolling === false ? "Roll Dice" : "Rolling..."}
        </button>
      </div>
    );
  }
}

export default RollDice;
