import React, { useState, useRef } from 'react';
import face1 from '../../imagens/face1.png';
import face2 from '../../imagens/face2.png';
import face3 from '../../imagens/face3.png';
import face4 from '../../imagens/face4.png';
import face5 from '../../imagens/face5.png';
import face6 from '../../imagens/face6.png';

import './index.css';

function Dices() {
  const [result, setResult] = useState('Click this button below to start');
  const [history, setHistory] = useState([]);

  const user1 = useRef(null);
  const user2 = useRef(null);
  const resultDice = useRef(null);
  const titleHistoryResult = useRef(null);

  let diceImages = [face1, face2, face3, face4, face5, face6];

  const roll = () => {
    // Generate random number
    const firstRandomNum = Math.floor(Math.random() * 6);


    // Logic for this game


    user1.current.setAttribute('src', diceImages[firstRandomNum]);

  };

  const reset = () => {
    setHistory([]);
  };

  return (
    <>
      <div className='dice-wrapper'>
        <div className='dice-area'>
          <p>User 1</p>
          <img src={face1} ref={user1} alt='Dice' />
        </div>

      </div>
      <p className='result' ref={resultDice}>
        {result}
      </p>
      <button onClick={roll} className='btn'>
        Roll the dice
      </button>
      <button onClick={reset} className='btn reset'>
        Reset
      </button>

    </>
  );
}

export default Dices;
