import React, { Component } from "react";
import ReactDOM from "react-dom";
import Dice from "./dice";
import "./styles.css";

class App extends Component {
  constructor() {
    super();
    this.state = {
      numDice: "1"
    };
  }

  handleDiceChange = (message) => {
    let num = this.state.numDice;
    if (num > 1 && message === "sub") {
      num--;
    } else if (num < 200 && message === "add") {
      num++;
    }
    console.log(num);
    this.setState({ numDice: num });
  };

  rollDoneCallback(num) {
    console.log(`Numero d ${num}`);
  }

  render() {
    const { numDice } = this.state;
    return (
      <div className="App">
        <div className="Controls"></div>
        <Dice number={numDice} rolldone={this.rollDoneCallback} />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<App />, rootElement);
